package com.core.controlstatements;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int number = 2;
		
		switch( number) {
		case 1 : System.out.println("Hi "); break;
		case 2: System.out.println("Hi to all the team lead "); break;
		case 3: System.out.println("Hi to Managers");
		default: System.out.println("Hi to all employeess");
			}
	}

}
